const d=window.location.href.split("#")[0];window.addEventListener("load",function(){document.querySelectorAll("div.article-content a").forEach(i=>{if(i.href.includes(d)){let e=i.href.split(d)[1];if(e&&e.startsWith("#")&&e.length>1){e=e.slice(1);const t=document.querySelector(`aside.aside#${e} .aside-box`);t&&(i.addEventListener("mouseenter",()=>{t.classList.add("aside--highlight-linked-box")}),i.addEventListener("mouseleave",()=>{t.classList.remove("aside--highlight-linked-box")}))}}})});
